import React,{Component} from 'react';
import { StyleSheet,View,Text,TouchableHighlight } from 'react-native';
import { Card, Button,Title,Paragraph  } from 'react-native-paper';
import SwitchBtn from '../components/switch';
import ValSlider from '../components/slider'
import Dimmer from '../components/dimmer';
import { MaterialIcons } from '@expo/vector-icons';

class ControlScreen extends Component{
    render(){
        return (
            <View style = {styles.mybg} >
                 
               
                <View style = {styles.container}>
                   <TouchableHighlight style={styles.tile} onLongPress = {console.log("a")}  underlayColor = "white">
                    <View >
                        <Text style = {styles.myheading}>Switch 1</Text>
                        <SwitchBtn />
                    </View>
                   </TouchableHighlight>
                   {/* <TouchableHighlight style={styles.tile} onLongPress = {console.log("onHoldingButton")}  underlayColor = "white">
                    <View >
                    <Text  style = {styles.myheading}>Switch 2</Text>
                    <SwitchBtn />
                    </View>
                   </TouchableHighlight> */}
                 
                </View>
            </View>
       );
    }  
}

const styles = StyleSheet.create({
    mybg: {
        backgroundColor: '#dfe5f0',
        flex : 1,
    },
    container: {
        flex: 0.185,
        flexWrap: 'wrap',
        flexDirection: 'row',
        // alignItems:'center',
        justifyContent: 'space-evenly',
        // backgroundColor:'grey'
      

        
    },
    tile: {
        height: '80%',
        width:'45%',
        backgroundColor:'lightblue',
        justifyContent: 'center',
        marginTop: '4%'

    },
    rgbtile: {
        height: '100%',
        width:'92%',
        backgroundColor:'lightblue',
        justifyContent: 'center',
        marginTop: '4%'

    },
    sldr: {
       alignItems: 'center'

    },
    myheading: {
        fontSize:18,
        fontWeight: 'bold',
        flexWrap: 'wrap',
        textAlign: 'center'
    }
});

export default ControlScreen;
