import React,{Component} from 'react';
import { StyleSheet,View,Text,TouchableHighlight,Modal } from 'react-native';
import { Card, Button,Title,Paragraph,TextInput  } from 'react-native-paper';
import SwitchBtn from '../components/switch';
import ValSlider from '../components/slider'
import Dimmer from '../components/dimmer';
import { MaterialIcons } from '@expo/vector-icons';

class ControlScreen extends Component{
    state = {
        modalVisible: false,
     }
     toggleModal(visible) {
        this.setState({ modalVisible: visible });
     }
    render(){
        return (
            <View style = {styles.mybg} >
                 <Modal animationType = {"slide"} transparent = {false}
               visible = {this.state.modalVisible}
               onRequestClose = {() => { console.log("Modal has been closed.") } }>
               
               <View style = {styles.modal}>
               <Text style = {styles.myheading}>BUTTON EDITOR</Text>
               <View style = {{width: 300, height: 70}}>
               <TextInput
                label="Status Off"
                placeholder = "Light Off"
                />
               </View>
               <View style = {{width: 300, height: 70}}>
               <TextInput
                label="Status On"
                placeholder = "Light On"
                />
               </View>
               <View style = {{width: 300, height: 70}}>
               <TextInput
                label="Hexadecimal Code"
                placeholder = "012 235 110 11"
                />
               </View>
               <View style = {{width: 300, height: 70}}>
               <TextInput
                 label="Hexadecimal Code"
                placeholder = "012 235 110 11"
                />
               </View>
             
              
                  
                  <TouchableHighlight onPress = {() => {
                     this.toggleModal(!this.state.modalVisible)}}>
                     
                     <Text style = {{fontWeight:"bold"}}>Close Modal</Text>
                  </TouchableHighlight>
               </View>
            </Modal>
               
                <View style = {styles.container}>
                   <TouchableHighlight style={styles.tile} onLongPress = {() => {this.toggleModal(true)}}  underlayColor = "white">
                    <View >
                        <Text style = {styles.myheading}>Switch 1</Text>
                        <SwitchBtn />
                    </View>
                   </TouchableHighlight>
                   {/* <TouchableHighlight style={styles.tile} onLongPress = {console.log("onHoldingButton")}  underlayColor = "white">
                    <View >
                    <Text  style = {styles.myheading}>Switch 2</Text>
                    <SwitchBtn />
                    </View>
                   </TouchableHighlight> */}
                 
                </View>
            </View>
       );
    }  
}

const styles = StyleSheet.create({
    mybg: {
        backgroundColor: '#dfe5f0',
        flex : 1,
    },
    modal: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: '#f7021a',
        padding: 100
     },
    container: {
        flex: 0.185,
        flexWrap: 'wrap',
        flexDirection: 'row',
        // alignItems:'center',
        justifyContent: 'space-evenly',
        // backgroundColor:'grey'
      

        
    },
    tile: {
        height: '80%',
        width:'45%',
        backgroundColor:'lightblue',
        justifyContent: 'center',
        marginTop: '4%'

    },
    rgbtile: {
        height: '100%',
        width:'92%',
        backgroundColor:'lightblue',
        justifyContent: 'center',
        marginTop: '4%'

    },
    sldr: {
       alignItems: 'center'

    },
    myheading: {
        fontSize:18,
        fontWeight: 'bold',
        flexWrap: 'wrap',
        textAlign: 'center'
    }
});

export default ControlScreen;
